import {Bank, Post, Role, User, Comment, Mail} from './model'
import {emailValidate} from './utils'

const _ = require("lodash");

export default {
  Query: {

    // Login & Logout
    async Login(root, {
      username, 
      password
    }) {
      let start = Date.now()

     

      // let user = null;
      // if(emailValidate().test(username)){
      //   user = await User.findOne({email: username})
      // }else{
      //   user = await User.findOne({username})
      // }

      let user = emailValidate().test(username) ?  await User.findOne({email: username}) : await User.findOne({username})

      if(user === null){
        return {
          status: false,
          messages: "xxx", 
          data:{
            _id: "",
            username: "",
            password: "",
            email: "",
            displayName: "",
            roles:[]
          },
          executionTime: `Time to execute = ${
            (Date.now() - start) / 1000
          } seconds`
        }
      }

      // update lastAccess
      await User.findOneAndUpdate({
        _id: user._doc._id
      }, {
        lastAccess: Date.now()
      }, {
        new: true
      })

      user = emailValidate().test(username) ?  await User.findOne({email: username}) : await User.findOne({username})

      let roles = await Promise.all(_.map(user.roles, async(_id)=>{
        let role = await Role.findById(_id)
        console.log("_id", _id, role)
        return role.name
      }))
      user = {...user._doc,  roles}

      console.log("Login vvv", user )

      return {
        status: true,
        messages: "", 
        data: user,
        executionTime: `Time to execute = ${
          (Date.now() - start) / 1000
        } seconds`
      }
    },
    // Login & Logout

