      let start = Date.now()

      console.log("FindUser filter : ", JSON.parse(JSON.stringify(filter.q)),
                  `Time to execute = ${ (Date.now() - start) / 1000 } seconds` )

      let q = filter.q.split("=")


      let data = await User.find({[q[0]]:q[1]});

      return {
