import Room from './model/RoomModel'
export default {
  Query: {
    async rooms() {
      return {
        status:true,
        data:await Room.find()
      }
    },
    async room(root, {
      _id
    }) {
      return {
        status:true,
        data:await Room.findById(_id)
      }
    },
    // async m(root, {
    //   _id
    // }) {
    //   return await Room.findById(_id);
    // },
  },
  Mutation: {
    async createRoom(root, {
      input
    }) {
      console.log("createRoom :", input)
      return await Room.create(input);
    },
    async updateRoom(root, {
      _id,
      input
    }) {
      console.log("updateRoom :", _id, input)
      return await Room.findOneAndUpdate({
        _id
      }, input, {
        new: true
      })
    },
    async deleteRoom(root, {
      _id
    }) {
      console.log("deleteRoom :", _id)
      return await Room.findByIdAndRemove(_id)
    }
  }
};

